INSERT INTO courses(id, theme, subject, status, online, address, teacher_id, individual, cost, start_date, finish_date, description, average_rating) VALUES
(1, "IT", "Google IT Support", "NOT_STARTED", "ONLINE", NULL, 2, false, 20, STR_TO_DATE("03.10.2022 15:40", "%d.%m.%Y %H:%i"), STR_TO_DATE("31.10.2022 15:40", "%d.%m.%Y %H:%i"), "This is your path to a career in IT. In this program, you’ll learn in-demand skills that will have you job-ready in less than 6 months. No degree or experience required.", NULL),
(2, "IT", "Oracle Cloud Infrastructure Foundations", "NOT_STARTED", "ONLINE", NULL, 3, false, 40, STR_TO_DATE("10.09.2022 12:00", "%d.%m.%Y %H:%i"), STR_TO_DATE("01.10.2022 12:00", "%d.%m.%Y %H:%i"), "Welcome to the course OCI Foundations Course. This course is a starting point to prepare you for the Oracle Cloud Infrastructure Foundations Associate Certification. Begin with an introduction of the OCI platform, and then dive into the core primitives, compute, storage, networking, identity, databases, security, and more.", NULL),
(3, "IT", "Technical Support Specialist (IBM)", "NOT_STARTED", "ONLINE", NULL, 3, false, 77, STR_TO_DATE("05.11.2022 12:00", "%d.%m.%Y %H:%i"), STR_TO_DATE("17.12.2022 12:00", "%d.%m.%Y %H:%i"), "Launch your rewarding new career in tech. This program will prepare you with job-ready skills valued by employers in as little as 3 months. No degree or prior experience needed to get started.", NULL),
(4, "Business", "Financial markets", "NOT_STARTED", "ONLINE", NULL, 4, false, 105, STR_TO_DATE("10.11.2022 13:00", "%d.%m.%Y %H:%i"), STR_TO_DATE("22.11.2022 13:00", "%d.%m.%Y %H:%i"), "An overview of the ideas, methods, and institutions that permit human society to manage risks and foster enterprise.  Emphasis on financially-savvy leadership skills. Description of practices today and analysis of prospects for the future. Introduction to risk management and behavioral finance principles to understand the real-world functioning of securities, insurance, and banking industries.  The ultimate goal of this course is using such industries effectively and towards a better society.", NULL),
(5, "Business", "Brand Management: Aligning Business, Brand and Behaviour", "NOT_STARTED", "OFFLINE", "Senate House, Malet St, London WC1E 7HU, Great Britain", 5, false, 60, STR_TO_DATE("26.11.2022 12:00", "%d.%m.%Y %H:%i"), STR_TO_DATE("24.12.2022 12:00", "%d.%m.%Y %H:%i"), "Professor Nader Tavassoli of London Business School contrasts traditional approaches to branding - where brands are a visual identity and a promise to customers - to brands as a customer experience delivered by the entire organisation. The course offers a brand workout for your own brands, as well as guest videos from leading branding professionals.", NULL);
INSERT INTO lessons(id, theme, subject, teacher_id, course_id, status, address, individual, max_students_number, description, cost, durability, lesson_date, fixed_salary, unfixed_salary, average_rating) VALUES
(1, "IT", "Technical Support Fundamentals", 2, 1, "NOT_STARTED", NULL, false, NULL, "This course is the first of a series that aims to prepare you for a role as an entry-level IT Support Specialist. In this course, you’ll be introduced to the world of Information Technology, or IT. You’ll learn about the different facets of Information Technology, like computer hardware, the Internet, computer software, troubleshooting, and customer service. This course covers a wide variety of topics in IT that are designed to give you an overview of what’s to come in this certificate program.", 4, 60, STR_TO_DATE("03.10.2022 15:40", "%d.%m.%Y %H:%i"), 80, 70, NULL),
(2, "IT", "The Bits and Bytes of Computer Networking", 2, 1, "NOT_STARTED", NULL, false, NULL, "This course is designed to provide a full overview of computer networking. We’ll cover everything from the fundamentals of modern networking technologies and protocols to an overview of the cloud to practical applications and network troubleshooting.", 4, 60, STR_TO_DATE("10.10.2022 15:40", "%d.%m.%Y %H:%i"), 80, 70, NULL),
(3, "IT", "Operating Systems and You: Becoming a Power User", 2, 1, "NOT_STARTED", NULL, false, NULL, "In this course -- through a combination of video lectures, demonstrations, and hands-on practice -- you’ll learn about the main components of an operating system and how to perform critical tasks like managing software and users, and configuring hardware.", 4, 60, STR_TO_DATE("17.10.2022 15:40", "%d.%m.%Y %H:%i"), 80, 70, NULL),
(4, "IT", "System Administration and IT Infrastructure Services", 2, 1, "NOT_STARTED", NULL, false, NULL, "This course will transition you from working on a single computer to an entire fleet. Systems administration is the field of IT that’s responsible for maintaining reliable computers systems in a multi-user environment. In this course, you’ll learn about the infrastructure services that keep all organizations, big and small, up and running. We’ll deep dive on cloud so that you’ll understand everything from typical cloud infrastructure setups to how to manage cloud resources. You'll also learn how to manage and configure servers and how to use industry tools to manage computers, user information, and user productivity. Finally, you’ll learn how to recover your organization’s IT infrastructure in the event of a disaster.", 4, 60, STR_TO_DATE("24.10.2022 15:40", "%d.%m.%Y %H:%i"), 80, 70, NULL),
(5, "IT", "IT Security: Defense against the digital dark arts", 2, 1, "NOT_STARTED", NULL, false, NULL, "This course covers a wide variety of IT security concepts, tools, and best practices. It introduces threats and attacks and the many ways they can show up. We’ll give you some background of encryption algorithms and how they’re used to safeguard data. Then, we’ll dive into the three As of information security: authentication, authorization, and accounting. We’ll also cover network security solutions, ranging from firewalls to Wifi encryption options. The course is rounded out by putting all these elements together into a multi-layered, in-depth security architecture, followed by recommendations on how to integrate a culture of security into your organization or team.", 4, 60, STR_TO_DATE("31.10.2022 15:40", "%d.%m.%Y %H:%i"), 80, 70, NULL),
